            if (forgin != "")
            {
                int create_forginkeytbl = d2.update_method_wo_parameter(forgin, "Text");
            }
            //03.10.17 bb
            procedureQ = string.Empty;
            if (IsStoredProcedureAvailable("SplitString"))
                procedureType = 1;
            else
                procedureType = 0;
            procedureQ = ((procedureType == 0) ? "create " : "alter ") + " FUNCTION SplitString (          @Input NVARCHAR(MAX),      @Character CHAR(1))RETURNS @Output TABLE Item NVARCHAR(1000)) AS BEGIN DECLARE @StartIndex INT, @EndIndex INT SET @StartIndex = 1 IF SUBSTRING(@Input, LEN(@Input) - 1, LEN(@Input)) <> @Character BEGIN SET @Input = @Input + @Character END WHILE CHARINDEX(@Character, @Input) > 0 BEGIN SET @EndIndex = CHARINDEX(@Character, @Input) INSERT INTO @Output(Item) SELECT SUBSTRING(@Input, @StartIndex, @EndIndex - 1) SET @Input = SUBSTRING(@Input, @EndIndex + 1, LEN(@Input)) END RETURN END GO";
            ExecuteProcedure("SplitString", procedureQ, procedureType);
        }
