            int.TryParse(d2.GetFunction("select LinkValue from New_InsSettings where linkname = 'Fee Yearwise' and user_code ='" + usercode + "' and college_code ='" + collegeCode + "'"), out retVal);
            if (retVal == 0)
            {
                dispalyValue = " Semester";
            }
            else if (retVal == 1)
            {
                dispalyValue = " Year";
            }
            else if (retVal == 2)
            {
                dispalyValue = " Term";
            }
        }
        catch { retVal = 0; }
