    protected Dictionary<string, int> getSubjectCode(string usercode)
    {
        Dictionary<string, int> dtSubj = new Dictionary<string, int>();
        try
        {
            string linkName = "";
            if (rbVocation.Checked == false)//modify barath 07.03.17
                linkName = "SubjectPriority";
            else
                linkName = "SubjectPriorityVocational";
            string selQ = " select LinkName,LinkValue from New_InsSettings where LinkName='" + linkName + "' and user_code ='" + usercode + "' and college_code ='" + ddl_college.SelectedValue + "'";
            DataSet dsval = d2.select_method_wo_parameter(selQ, "Text");
            if (dsval.Tables.Count > 0 && dsval.Tables[0].Rows.Count > 0)
            {
                string subjCode = Convert.ToString(dsval.Tables[0].Rows[0]["LinkValue"]);
                string[] splcode = subjCode.Split(',');
                if (splcode.Length > 0)
                {
                    for (int row = 0; row < splcode.Length; row++)
                    {
                        if (!dtSubj.ContainsKey(splcode[row]))
                            dtSubj.Add(splcode[row], row);
                    }
                }
            }
        }
        catch
        { }
        return dtSubj;
    }
    protected bool priorityCheck(ref Dictionary<string, string> dtsubjcode)
    {
        bool check = false;
        try
        {
            //txt_cutof.Text = string.Empty; txt_cutofmark.Text = string.Empty;
            Dictionary<string, int> dtsubjSett = getSubjectCode(usercode);
            if (dtsubjSett.Count > 0)
            {
                int setSubCount = 0;
                int.TryParse(Convert.ToString(dtsubjSett.Count), out setSubCount);
                int selCnt = 0;
                if (dtsubjcode.Count > 0)
                {
                    double cutOff = 0;
                    double MarkObtained = 0;
                    double MaximumMark = 0;
                    double mathsmin = 0; double mathmax = 0;
                    double cutoffmark = 0; double cutoffmarkmin = 0; double cutoffmarkmax = 0;
                    foreach (KeyValuePair<string, string> selCode in dtsubjcode)
                    {
                        string[] subjectcode = selCode.Key.Split('$');
                        if (dtsubjSett.ContainsKey(subjectcode[0]))
                        {
                            selCnt++;
                            double markObt = 0;
                            double maxMark = 0;
                            double withoutmathsmin, withoutmathmax = 0;
                            if (subjectcode[1].ToUpper().Trim().Contains("MATH") || subjectcode[1].ToUpper().Trim().Contains("MATHS") || subjectcode[1].ToUpper().Trim().Contains("MATHEMATICS"))//cut off calculation barath 07.03.17
                            {
                                double.TryParse(Convert.ToString(selCode.Value.ToString().Split('-')[0]), out mathsmin);
                                double.TryParse(Convert.ToString(selCode.Value.ToString().Split('-')[1]), out mathmax);
                            }
                            else
                            {
                                double.TryParse(Convert.ToString(selCode.Value.ToString().Split('-')[0]), out withoutmathsmin);
                                double.TryParse(Convert.ToString(selCode.Value.ToString().Split('-')[1]), out withoutmathmax);
                                cutoffmarkmin += withoutmathsmin;
                                cutoffmarkmax += withoutmathmax;
                            }
                            //pcm calculation
                            double.TryParse(Convert.ToString(selCode.Value.ToString().Split('-')[0]), out markObt);
                            double.TryParse(Convert.ToString(selCode.Value.ToString().Split('-')[1]), out maxMark);
                            MarkObtained += markObt;
                            MaximumMark += maxMark;
                        }
                    }
                    if (setSubCount == selCnt)
                    {
                        check = true;//pcm calculation
                        cutOff = (MarkObtained / MaximumMark) * 100;
                        txt_cutof.Text = Convert.ToString(Math.Round(Convert.ToDecimal(cutOff), 2));
                        //cut of calculation
                        if (mathsmin != 0 && mathmax != 0)
                        {
                            cutoffmark = (cutoffmarkmin / cutoffmarkmax * 100) + (mathsmin / mathmax * 100);
                            txt_cutofmark.Text = Convert.ToString(Math.Round(Convert.ToDecimal(cutoffmark), 2));
                        }
                        else
                        {
                            check = false;
                        }
                    }
                    else
                        check = false;
                }
                else
                    check = false;
            }
            else
                check = true;
        }
        catch { }
        return check;
    }
